import React from "react"
import { ComposableMap, Geographies, Geography, ZoomableGroup } from "react-simple-maps"
import { useState, useEffect } from "react";

const geoUrl =
  "https://raw.githubusercontent.com/deldersveld/topojson/master/world-countries.json"

function MapChart() {

    const [clickedCountry, setClickedCountry] = useState("");
    const accessToken = 'BQCDgupXVKFrnSKkmMSdpKA_8U76GtBEzEtNPyvGwLYtviIGCj0ljXKDzZsay93e9aeu2wQcl_8vGtHQUfiLQAPgXLHE73d5NJndMHdOaAkjivlWgSdKO4seBiKwN9-QIzMszFm3rF9cHqO73eTA5WPfMMExs0XRvEQCzSLrFCMq0wg'

    useEffect (() => {
      fetch('https://api.spotify.com/v1/playlists/37i9dQZEVXbKM896FDX8L1', {
            method: 'GET', headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + accessToken
            }
        })
            .then((response) => {
                console.log(response.json().then(
                    (data) => { console.log(data) }
                ));
            });


    }, [])



    const handleClick = (geo) => {
        setClickedCountry(geo.properties.name);}

  return (
    <>
    <ComposableMap>
      <Geographies geography={geoUrl}>
        {({ geographies }) =>
          geographies.map((geo) => (
            <Geography 
            key={geo.rsmKey}
            geography={geo}
            onClick={() => handleClick(geo)}
            style={{
                default: {
                  fill: "#ffffff",
                  outline: 'none'
                },
                hover: {
                  fill: "#1ed760",
                  outline: 'none'
                },
                pressed: {
                  fill: "#1db954",
                  outline: 'none'
                },
              }}
    />
          ))
        }
      </Geographies>
    </ComposableMap>
    <div>{clickedCountry}</div>
    </>
  )
}

export default MapChart;
